DO $$
BEGIN
  IF NOT EXISTS (
    SELECT 1 FROM pg_type WHERE typname = 'purchase_kind'
  ) THEN
    CREATE TYPE purchase_kind AS ENUM
      ( 'Cash'
      , 'Credit'
      , 'Debit'
      , 'ACH'
      , 'Wire'
      );
  END IF;
END $$;

DROP TABLE IF EXISTS purchases CASCADE;

CREATE TABLE purchases
  ( id UUID PRIMARY KEY UNIQUE DEFAULT uuid_generate_v1mc()
  , customer_id UUID NOT NULL REFERENCES CUSTOMERS
  , amount NUMERIC(20, 2) NOT NULL
  , purchase_kind purchase_kind NOT NULL
  , notes jsonb
  , created_at TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT now()
  , updated_at TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT now()
  );

CREATE TRIGGER purchases_insert BEFORE INSERT ON purchases FOR EACH ROW EXECUTE PROCEDURE create_timestamps();
